mapper.select:
SELECT temp2.* FROM (SELECT rownum rnm, temp.* FROM (SELECT b.seq, b.name, b.id, b.email, b.subject, b.content, b.hit, b.bcode, DECODE(TO_CHAR(b.logtime, 'yymmdd'), TO_CHAR(SYSDATE, 'yymmdd'), TO_CHAR(b.logtime, 'hh24:mi:ss'), TO_CHAR(b.logtime, 'yy.mm.dd')) logtime, r.rseq, r.REF, r.lev, r.step, r.pseq, r.reply FROM board b, reboard r WHERE b.seq = r.seq AND bcode = #{bcode}
AND b.subject like '%'||#{word}||'%'
AND b.${key} = #{word}
ORDER BY ref desc, step) temp WHERE ROWNUM <= #{end} ) temp2 WHERE temp2.rnm > #{start}

mapper.update:
UPDATE reboard SET step = step+1
WHERE ref = #{ref}
AND step > #{step}
